import React, { useState, useEffect, useMemo } from "react";
import { useTable } from "react-table";
import Pagination from '@mui/material/Pagination'; 
import UserServices from "../../services/UserServices";
import { CircularProgress, Stack, Typography } from "@mui/material";


const SitterListTestPagination = (props) => {
    const [users, setUsers] = useState([]);

    const [page, setPage] = useState(1);
    const [count, setCount] = useState(0);
    const [pageSize, setPageSize] = useState(6);
    const [searchAddress, setSearchAddress] = useState("");
    const [loading, setLoading] = useState(false);

  const pageSizes = [6, 9];

  const onChangeSearchAddress = (e) => {
    const searchAddress = e.target.value;
    setSearchAddress(searchAddress);
  };

  const getRequestParams = (searchAddress, page, pageSize) => {
    let params = {};
    if (searchAddress) {
      params["address"] = searchAddress;
    }
    if (page) {
      params["page"] = page - 1;
    }
    if (pageSize) {
      params["size"] = pageSize;
    }

    return params;
  };
  
    const retrieveTutorials = () => {
      setLoading(true);
      const params = getRequestParams(searchAddress,page, pageSize);

      UserServices.getPaginationAll(params)
        .then((response) => {
          const { user, totalPages } = response.data;
          setLoading(false);
          setUsers(user);
          setCount(totalPages);
          
          console.log(response.data);
        })
        .catch((e) => {
          setLoading(false);
          console.log(e);
        });
    };

    useEffect(retrieveTutorials, [page, pageSize]);

    const handlePageChange = (event, value) => {
      setPage(value);
    };
  
    const handlePageSizeChange = (event) => {
      setPageSize(event.target.value);
      setPage(1);
    };

    const columns = useMemo(
        () => [
          {
            Header: "Contact Name",
            accessor: "contactname",
          },
          {
            Header: "Company Name",
            accessor: "company",
          },
          {
            Header: "Email",
            accessor: "email",
          },
          {
            Header: "Address",
            accessor: "address",
          },
          {
            Header: "Open Time",
            accessor: "open",
          },
          {
            Header: "Charges",
            accessor: "chargesperhour",
          },
        ],
        []
      );
    
      const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
      } = useTable({
        columns,
        data: users,
      });

    return (
        <>
          <div className="row">
          <div className="col-md-8">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by Address"
            value={searchAddress}
            onChange={onChangeSearchAddress}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={retrieveTutorials}
            >
              Search
            </button>
          </div>
        </div>
      </div>
      </div>
          <div className="mt-3 mb-3">
            {"Items per Page: "}
            <select onChange={handlePageSizeChange} value={pageSize}>
              {pageSizes.map((size) => (
                <option key={size} value={size}>
                  {size}
                </option>
              ))}
            </select> 
          </div>
          {loading ? (
             <Stack  style={{
              position: 'absolute', left: '50%', top: '50%',
              transform: 'translate(-50%, -50%)'
            }}>
              <CircularProgress />
            </Stack>
            // <Stack spacing={1}>
            //   <Skeleton variant="rectangular" width="100%" height={60} />
            //   <Skeleton variant="rectangular" width="100%" height={60} />
            //   <Skeleton variant="rectangular" width="100%" height={60} />
            //   <Skeleton variant="rectangular" width="100%" height={60} />
            // </Stack>
            
            ) : 
            <div className="table-responsive">
          <table className="table table-striped table-bordered table-hover" {...getTableProps()} >
            <thead>
              {headerGroups.map((headerGroup) => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map((column) => (
                    <th {...column.getHeaderProps()}>
                      {column.render("Header")}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            
            <tbody {...getTableBodyProps()}>
              {rows.map((row, i) => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map((cell) => {
                      return (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      );
                    })}
                  </tr>
                );
              })}             
            </tbody>         
          </table>  
          <Typography>Page: {page}</Typography>
          <div style={{
              position: 'absolute', left: '50%',
              transform: 'translate(-50%, -50%)'
            }}>
              <Pagination 
                count={count}
                page={page}
                siblingCount={1}
                boundaryCount={1}
                variant="outlined"
                color="secondary"
                onChange={handlePageChange}
                showFirstButton showLastButton
              />
          </div>
          </div>
           }      
      </>
      );
    };
    export default SitterListTestPagination;
