import http from "../http-common";
  
const getAll = () => {
  return http.get("/users");
};

const get = id => {
  return http.get(`/users/${id}`);
};

const create = data => {
  console.log(data)
  return http.post("/users", data);
};

const update = (id, data) => {
  return http.put(`/users/${id}`, data);
};

const remove = id => {
  return http.delete(`/users/${id}`);
};

const getNearestLocation = (address, latitude, longitude) => {
  return http.get(`/users/${address}/${latitude}/${longitude}`);
}

const UserServices =  {
  getAll,
  get,
  create,
  update,
  remove
};

export default  UserServices;
