import React, { useEffect, useRef } from 'react'

const SearchSitter = () => {
    const effectRan = useRef(false);

    //Getting Current geo locations for search box address
var  latElGeo = document.querySelector( '.lat' ),
longElGeo = document.querySelector( '.lng' ),
x =  document.getElementById("demo");

window.onload = function getLocation() {
if (navigator.geolocation) {
   navigator.geolocation.getCurrentPosition(showPosition);
} else { 
   x.innerHTML = "Geolocation is not supported by this browser.";
}
}
function showPosition(position) {
latElGeo.value = position.coords.latitude;
longElGeo.value = position.coords.longitude;
}
// End of Getting Current geo locations for search box address 

    useEffect(() => {
		if(effectRan.current === false){
		const script = document.createElement('script');
	  
		script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyAfL2oULbnrWbl_G-WdVcxGH8TfEme8dhk&libraries=places&callback=initMap";
		script.async = true;  
		document.body.appendChild(script);  
		return () => {
		  document.body.removeChild(script);
		  effectRan.current = true;
		}
	}
	  }, []);

  return (
    <div>
        <h4 className="text-center">Search Sitter</h4>
        <div className="container">
        <form action="/searchsitter" method="get">
          <div className="input-group">
            <input type="search" name="address" id="map-search" className="form-control rounded" placeholder="Search" aria-label="Search" aria-describedby="search-addon" required="required" />
            <input type="text" name="latitude" id="lat" className="form-control lat" />
            <input type="text" name="longitude" id="lng" className="form-control lng" />
            <button type="submit" className="btn btn-outline-primary">search</button>	  
          </div>
        </form>
        <div id="floating-panel">
        </div>
      </div>

       {/************  Show list of sitter with nearest location  ***********************/}

      <div className="container" style={{marginTop: '10px'}}>
        <div className="row">
          <div className="col-sm-6">
            <a href="#" className="nav-link">
              <div className="card" style={{width: '33rem'}}>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-4">
                      <img src="images/" alt="Card image" style={{height: '100px', width: '100px'}} />
                    </div>
                    <div className="col-sm-8">
                      <h6 className="card-subtitle text-muted">Address</h6>
                      <p className="card-text text-muted">Hours: <span className="text-success">Open.</span> Open  Time</p>
                      <p className="card-text text-muted">Charge/hr : Charges</p>								
                    </div>
                  </div>
                </div>
              </div>	
            </a>					
          </div>
          <div className="col-sm-6">
            {/*Google map*/}
            <div id="map-canvas" />
          </div>
        </div>
      </div>
    </div>
  )
}

export default SearchSitter;
