import React, { useState, useEffect, useMemo, useRef } from "react";
import { useTable, useSortBy } from "react-table";
import UserServices from "../../services/UserServices";

const SitterListTestPagination = (props) => {
    const [users, setTutorials] = useState([]);
    const tutorialsRef = useRef();
    tutorialsRef.current = users;

    const [page, setPage] = useState(1);
    const [count, setCount] = useState(0);
    const [pageSize, setPageSize] = useState(3);

  const pageSizes = [3, 6, 9];

  const getRequestParams = (page, pageSize) => {
    let params = {};

    if (page) {
      params["page"] = page - 1;
    }
    if (pageSize) {
      params["size"] = pageSize;
    }

    return params;
  };
  
    useEffect(() => {
      retrieveTutorials();
    }, []);
  
    const retrieveTutorials = () => {
      const params = getRequestParams(page, pageSize);

      UserServices.getPaginationAll(params)
        .then((response) => {
          const { users, totalPages } = response.data;

          setTutorials(users);
          setCount(totalPages);
  
          console.log(response.data);
        })
        .catch((e) => {
          console.log(e);
        });
    };

    const columns = useMemo(
        () => [
          {
            Header: "Contact Name",
            Footer: "Contact Name",
            accessor: "contactname",
          },
          {
            Header: "Company Name",
            Footer: "Company Name",
            accessor: "company",
          },
          {
            Header: "Email",
            Footer: "Email",
            accessor: "email",
          },
          {
            Header: "Address",
            Footer: "Address",
            accessor: "address",
          },
          {
            Header: "Open Time",
            Footer: "Open Time",
            accessor: "open",
          },
          {
            Header: "Charges",
            Footer: "Charges",
            accessor: "chargesperhour",
          },
        ],
        []
      );
    
      const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        //footerGroups,
        rows,
        prepareRow,
      } = useTable({
        columns,
        data: users,
      }, useSortBy);

    return (
        <div>
          <p></p>
          <table
            className="table table-striped table-bordered"
            {...getTableProps()}
          >
            <thead>
              {headerGroups.map((headerGroup) => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map((column) => (
                    <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                      {column.render("Header")}
                      <span>
                        {column.isSorted ? (column.isSortedDesc ? 'ðŸ”¼':'ðŸ”½'): ''}
                      </span>
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {rows.map((row, i) => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map((cell) => {
                      return (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
            {/* <tfoot>
              {
                footerGroups.map(footerGroup =>(
                  <tr {...footerGroup.getFooterGroupProps()}>
                  {
                  footerGroup.headers.map(column =>(
                    <th {...column.getFooterProps}>
                      {
                        column.render('Footer')
                      }
                    </th>
                  ))}</tr>
                ))
              }
            </tfoot> */}
          </table> 
      </div>
      );
    };
    

    export default SitterListTestPagination;
