/*global google*/
import React, {useState, useEffect, useRef} from "react";
import {useForm} from 'react-hook-form';

import UserServices from "../../services/UserServices";

function initMap() {
	var mapOptions, map, marker, searchBox,
	infoWindow = '',
	addressEl = document.getElementById( 'map-search' ),
	latEl = document.getElementById( 'latitude' ),
	longEl = document.getElementById( 'longitude' ),
	element = document.getElementById( 'map-canvas' );

mapOptions = {
	zoom: 8,
	center: new google.maps.LatLng( 0,0 ),
	disableDefaultUI: false, // Disables the controls like zoom control on the map if set to true
	scrollWheel: true, // If set to false disables the scrolling on the map.
	draggable: true, // If set to false , you cannot move the map around.
};
map = new google.maps.Map( element, mapOptions ); // Till this like of code it loads up the map.
marker = new google.maps.Marker({
	position: mapOptions.center,
	map: map,
	// icon: 'http://pngimages.net/sites/default/files/google-maps-png-image-70164.png',
	draggable: true
});

 searchBox = new google.maps.places.SearchBox(addressEl);
/**
 * When the place is changed on search box, it takes the marker to the searched location.
 */
google.maps.event.addListener( searchBox, 'places_changed', function () {
	var places = searchBox.getPlaces(),
		bounds = new google.maps.LatLngBounds(),
		i, place, lat, long,
		addresss = places[0].formatted_address;
		console.log(addresss);
		
// eslint-disable-next-line
	for( i = 0; place = places[i]; i++ ) {
		bounds.extend( place.geometry.location );
		marker.setPosition( place.geometry.location );  // Set marker position new.
	}

	map.fitBounds( bounds );  // Fit to the bound
	map.setZoom( 15 ); // This function sets the zoom to 15, meaning zooms to level 15.

	lat = marker.getPosition().lat();
	long = marker.getPosition().lng();
	console.log(lat, long);
	latEl.value = lat;
	longEl.value = long;

	if ( infoWindow ) {
		infoWindow.close();
	}
	infoWindow = new google.maps.InfoWindow({
		content: addresss
	});

	infoWindow.open( map, marker );
} );
/**
 * Finds the new position of the marker when the marker is dragged.
 */
google.maps.event.addListener( marker, "dragend", function () {
	var lat, long, address;

	console.log( 'i am dragged' );
	lat = marker.getPosition().lat();
	long = marker.getPosition().lng();

	var geocoder = new google.maps.Geocoder();
	geocoder.geocode( { latLng: marker.getPosition() }, function ( result, status ) {
		if ( 'OK' === status ) {  // This line can also be written like if ( status == google.maps.GeocoderStatus.OK ) {
			address = result[0].formatted_address;
			//addressEl.value = address;
			latEl.value = lat;
			longEl.value = long;

		} else {
			console.log( 'Geocode was not successful for the following reason: ' + status );
		}
		if ( infoWindow ) {
			infoWindow.close();
		}
		infoWindow = new google.maps.InfoWindow({
			content: address
		});

		infoWindow.open( map, marker );
	} );
});
}

window.initMap = initMap;

const TestFormValidation = () => {

	function initMap() {
		var mapOptions, map, marker, searchBox,
		infoWindow = '',
		addressEl = document.getElementById( 'map-search' ),
		latEl = document.getElementById( 'latitude' ),
		longEl = document.getElementById( 'longitude' ),
		element = document.getElementById( 'map-canvas' );
	
	mapOptions = {
		zoom: 8,
		center: new google.maps.LatLng( 0,0 ),
		disableDefaultUI: false, // Disables the controls like zoom control on the map if set to true
		scrollWheel: true, // If set to false disables the scrolling on the map.
		draggable: true, // If set to false , you cannot move the map around.
	};
	map = new google.maps.Map( element, mapOptions ); // Till this like of code it loads up the map.
	marker = new google.maps.Marker({
		position: mapOptions.center,
		map: map,
		// icon: 'http://pngimages.net/sites/default/files/google-maps-png-image-70164.png',
		draggable: true
	});
	
	 searchBox = new google.maps.places.SearchBox(addressEl);
	/**
	 * When the place is changed on search box, it takes the marker to the searched location.
	 */
	google.maps.event.addListener( searchBox, 'places_changed', function () {
		var places = searchBox.getPlaces(),
			bounds = new google.maps.LatLngBounds(),
			i, place, lat, long,
			addresss = places[0].formatted_address;
			console.log(addresss);
			
	// eslint-disable-next-line
		for( i = 0; place = places[i]; i++ ) {
			bounds.extend( place.geometry.location );
			marker.setPosition( place.geometry.location );  // Set marker position new.
		}
	
		map.fitBounds( bounds );  // Fit to the bound
		map.setZoom( 15 ); // This function sets the zoom to 15, meaning zooms to level 15.
	
		lat = marker.getPosition().lat();
		long = marker.getPosition().lng();
		console.log(lat, long);
		latEl.value = lat;
		longEl.value = long;
	
		if ( infoWindow ) {
			infoWindow.close();
		}
		infoWindow = new google.maps.InfoWindow({
			content: addresss
		});
	
		infoWindow.open( map, marker );
	} );
	/**
	 * Finds the new position of the marker when the marker is dragged.
	 */
	google.maps.event.addListener( marker, "dragend", function () {
		var lat, long, address;
	
		console.log( 'i am dragged' );
		lat = marker.getPosition().lat();
		long = marker.getPosition().lng();
	
		var geocoder = new google.maps.Geocoder();
		geocoder.geocode( { latLng: marker.getPosition() }, function ( result, status ) {
			if ( 'OK' === status ) {  // This line can also be written like if ( status == google.maps.GeocoderStatus.OK ) {
				address = result[0].formatted_address;
				//addressEl.value = address;
				latEl.value = lat;
				longEl.value = long;
	
			} else {
				console.log( 'Geocode was not successful for the following reason: ' + status );
			}
			if ( infoWindow ) {
				infoWindow.close();
			}
			infoWindow = new google.maps.InfoWindow({
				content: address
			});
	
			infoWindow.open( map, marker );
		} );
	});
	}
	
	window.initMap = initMap;


	const effectRan = useRef(false);
    const {
        register,
        handleSubmit,
        formState: { errors },
      } = useForm();

      const inputRef = useRef(null);
    
      console.log("error: ",errors);
	
	useEffect(() => {
		if(effectRan.current === false){
		const script = document.createElement('script');
	  
		script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyAfL2oULbnrWbl_G-WdVcxGH8TfEme8dhk&libraries=places&callback=initMap";
		script.async = true;  
		document.body.appendChild(script);  
		return () => {
		  document.body.removeChild(script);
		  effectRan.current = true;
		}
	}
	  }, []);

	const initialUserService ={
		id: null,
		contactname: "",
		email: "",
		password: "",
		company: "",
		address: "",
		latitude: "",
		longitude: "",
		open: "",
		close: "",
		chargesperhour: "",
		logo: "//ssl.gstatic.com/accounts/ui/avatar_2x.png",
		enabled: true,
	};

	const [users, setUser] = useState(initialUserService);
	const [submitted, setSubmitted] = useState(false);
	const [loading, setLoading] = useState(false);

	// const handleInputChange = event => {
	// 	const {name, value} = event.target;
	// 	setUser({...users, [name]: value});	
	// };
	
	const saveUser = (e) =>{
		setLoading(true);
		var data = {
			contactname: users.contactname,
			email: users.email,
			password: users.password,
			company: users.company,
			address: users.address,
			latitude: users.latitude,
			longitude: users.longitude,
			open: users.open,
			close: users.close,
			chargesperhour: users.chargesperhour,
			logo: users.logo,
			enabled: users.enabled
		}

	UserServices.create(data)
		.then(response => {
			setUser({
				id: response.data.id,
				contactname: response.data.contactname,
				email: response.data.email,
				password: response.data.password,
				company: response.data.company,
				address: response.data.address,
				latitude: response.data.latitude,
				longitude: response.data.longitude,
				open: response.data.open,
				close: response.data.close,
				chargesperhour: response.data.chargesperhour,
				logo: response.data.logo,
				enabled: response.data.enabled
			});		
			setSubmitted(true);
			setLoading(false);
			console.log(response.data);
		})
		.catch(e =>{
			setLoading(false);
			console.log(e);
		})
	};

	const newUser = () => {
		setUser(initialUserService);
		setSubmitted(false);
	};

    return(
		<div className="submit-form">
			{submitted ? (
				<div>
					<h4>You submitted successfully!</h4>
					<button className="btn btn-success" onClick={newUser}>
						Add
					</button>
				</div>				
			) : (				
				<div className="container" style={{width: '40rem'}}>
				<div className="card">
				  <div className="card-header text-white bg-info text-center h3">
				  Sitter Registration Form
				  </div>
                  <form onSubmit={handleSubmit((data) => console.log(data))}>
				  <div className="card-body">
						<div className="form-group row">
							<label htmlFor="contactname" className="col-sm-3 col-form-label font-weight-bold"> Name :</label>
							<div className="col-sm-9">
								<input 
                                {... register("contactname", {
                                    required: "Contact name is required",
                                    minLength: {
                                    value: 3,
                                    message: "Contact name must be atleast 3 characters long",
                                    },
                                    maxLength: {
                                    value: 30,
                                    message: "Contact name must be atmost 30 characters long",
                                    },
                                    })}
                                type="text" id="contactname" name="contactname" className="form-control" placeholder="Contact name"/>								
							 <p style={{color: 'red'}}>
                                {errors.contactname?.message}
                                </p>
                            </div>
						</div>
						<div className="form-group row">
							<label htmlFor="email" className="col-sm-3 col-form-label font-weight-bold">Email :</label>
							<div className="col-sm-9">
								<input 
                                {... register("email", {
                                    required: "Email address is required",
                                    maxLength: {
                                    value: 64,
                                    message: "Email address must be atmost 64 characters long",
                                    },
                                    })}
                                type="text" name="email" className="form-control" id="email"  placeholder="Ex: john@gmail.com"/>	
                                 <p style={{color: 'red'}}>
                                {errors.email?.message}
                                </p>
                            </div>
						</div>
						<div className="form-group row">
							<label htmlFor="password" className="col-sm-3 col-form-label font-weight-bold">Password :</label>
							<div className="col-sm-9">
								<input 
                                {... register("password", {
                                    required: "Password is required",
                                    maxLength: {
                                    value: 15,
                                    message: "Password must be atmost 15 characters long",
                                    },
                                    })}
                                type="password" name="password" className="form-control" id="password"  placeholder="Password"/>	
                                <p style={{color: 'red'}}>
                                {errors.password?.message}
                                </p>
                            </div>
						</div>
						<div className="form-group row">
						   <label htmlFor="companyname" className="col-sm-3 col-form-label font-weight-bold">Company / Display Name :</label>
							  <div className="col-sm-9">
									   <input 
                                        {... register("company", {
                                            required: "Company name is required",
                                            maxLength: {
                                            value: 64,
                                            message: "Company name must be atmost 64 characters long",
                                            },
                                            })}
                                       type="text" name="company" className="form-control" id="company" placeholder="Enter your company name" />	  
                                     <p style={{color: 'red'}}>
                                        {errors.company?.message}
                                        </p>
                             </div>
						 </div>
						 <div className="form-group row">
							 <label htmlFor="map-search" className="col-sm-3 col-form-label font-weight-bold">Address :</label>
							   <div className="col-sm-9">
								   <input 
                                   {... register("address", {
                                    required: "Address is required",
                                    })}
                                   type="text" name="address" className="form-control" id="map-search"  placeholder="Enter your full address" />
								  <p style={{color: 'red'}}>
                                    {errors.address?.message}
                                    </p>
							 </div>
						 </div>
						 
						 <div className="form-group row">
							 <label htmlFor="location" className="col-sm-3 col-form-label font-weight-bold">Location :</label>
							   <div className="col-sm-9">
									   {/* Google map canvas */}
									<div id="map-canvas"></div>
										<div className="row"> 
											 <div className="form-group col-sm-6">
												  <label htmlFor="lat" className="font-weight-bold col-form-label">Latitude :</label>
													  <input {...register('latitude')}  type="text" name="latitude" id="latitude" className="form-control latitude"  placeholder="Latitude" defaultValue={"0"} ref={inputRef.current.value} />
											 </div>
											  <div className="form-group col-sm-6">
												  <label htmlFor="lng" className="font-weight-bold col-form-label">Longitude :</label>
													  <input {...register('longitude')} type="text" name="longitude" id="longitude" className="form-control longitude"  placeholder="Longitude" defaultValue={0} ref={inputRef.current.value} />
											 </div>
										 </div>
									 
									 <div className="row">
										  <div className="form-group col-sm-6">
												  <label htmlFor="opentime" className="font-weight-bold col-form-label">Opening Hour's :</label>
													  <input {...register('open')} type="text" name="open" id="open"  className="form-control" placeholder="Open" />
										 </div>
										 <div className="form-group col-sm-6">
												  <label htmlFor="closetime" className="font-weight-bold col-form-label">Closes Hour's :</label>
													  <input {...register('close')}  type="text" name="close" id="close"  className="form-control" placeholder="Closes" />
										 </div>
									 </div>
								 </div>
						 </div>	
						 
						 <div className="form-group row">
							 <label htmlFor="chargesperhour" className="col-sm-3 col-form-label font-weight-bold">Charges :</label>
							   <div className="col-sm-7">
									 <div className="input-group mb-3">
									  <input 
                                         {... register("chargesperhour", {
                                            required: "Charges is required",
                                            })}
                                      type="text" name="chargesperhour" id="chargesperhour" className="form-control"  placeholder="Enter your one hour charges" aria-label="Recipient's username" aria-describedby="basic-addon2" />								   
                                      <div className="input-group-append">
										<span className="input-group-text font-weight-bold" id="basic-addon2">/hr</span>
									  </div>
                                     
									</div>
								    <p style={{color: 'red'}}>
                                        {errors.chargesperhour?.message}
                                        </p>
							 </div>
						 </div>
						 
						   {/* <div className="form-group row">
							   <label className="col-sm-3 col-form-label font-weight-bold"></label>
								   <div className="col-sm-6"> */}
									   <input {...register('logo')} type="text" name="logo" className="" id="imagefile" style={{display: "none"}} />
								   {/* <div className="error"></div>
								   </div>
						</div> */}
						<input {...register('enabled')} type="hidden" name="enabled" id="enabled" className="form-control" value={true}  />
								 
						<button className="btn btn-primary" disabled={loading} type="submit">
						{loading && (
							<span className="spinner-border spinner-border-sm"></span>
						)}
						<span> Submit</span>
						</button>
						{/* <button  className="btn btn-success">Submit</button> */}
				  </div>
                  </form>
				</div>
			  </div>
			)}
		</div>
    );
}
export default TestFormValidation;
