/*global google*/
import React, { useEffect, useRef, useState } from 'react'
import UserServices from '../../services/UserServices';

  // Fetching records based on nearest location
  var  neighborhoods =[];
   neighborhoods = 
    users && users.map(user =>(
      {lat: parseFloat(user.latitude),lng :parseFloat(user.longitude)}
    ));
  
   var neighborhood = [];
   neighborhood.push(neighborhoods);
   console.log(neighborhood);

const labels = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
var  map, markers, myLatlng;

      function initMap() {
        
        console.log(neighborhood);
             //Getting Current geo locations for search box address
        var latElGeo = document.getElementById( 'lat' ),
        longElGeo = document.getElementById( 'lng' ),
        error =  document.getElementById("demo");
        
        window.onload = function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(showPosition);
        } else { 
            error.innerHTML = "Geolocation is not supported by this browser.";
        }
        }
        
        function showPosition(position) {
        setNativeValue(latElGeo, position.coords.latitude);	
        latElGeo.dispatchEvent(new Event('input', {bubbles: true}));
        setNativeValue(longElGeo, position.coords.longitude);	
        longElGeo.dispatchEvent(new Event('input', {bubbles: true}));
        }
 // End of Getting Current geo locations for search box address 


        var mapOptions;
        mapOptions = {
        center: new google.maps.LatLng(28.62, 77.36),
        zoom: 5,
        mapTypeId: google.maps.MapTypeId.ROADMAP,
        };
        var bounds = new google.maps.LatLngBounds(); 
        var infoWindow = new google.maps.InfoWindow();
        var infoWin = new google.maps.InfoWindow();
        map = new google.maps.Map(document.getElementById("map-canvas-search"), mapOptions);	
   
         //Current location svg image
        const svgMarker = {
                   path: "M10.453 14.016l6.563-6.609-1.406-1.406-5.156 5.203-2.063-2.109-1.406 1.406zM12 2.016q2.906 0 4.945 2.039t2.039 4.945q0 1.453-0.727 3.328t-1.758 3.516-2.039 3.070-1.711 2.273l-0.75 0.797q-0.281-0.328-0.75-0.867t-1.688-2.156-2.133-3.141-1.664-3.445-0.75-3.375q0-2.906 2.039-4.945t4.945-2.039z",
                   fillColor: "blue",
                   fillOpacity: 0.6,
                   strokeWeight: 0,
                   rotation: 0,
                   scale: 2,
                   anchor: new google.maps.Point(15, 30),
                 };
        
        var x = navigator.geolocation;
        x.getCurrentPosition(success);
        
        function success(position){
            var myLat = position.coords.latitude;
            var myLng = position.coords.longitude;
            var myLatlng2 = new google.maps.LatLng(myLat, myLng);
            
           var markerOption2 = new google.maps.Marker({
            position: myLatlng2,
            map: map,
            icon: svgMarker,
            });
           
           map.setCenter(myLatlng2);	
           let marker2 = new google.maps.Marker(markerOption2);
           marker2.setMap(map);
           bounds.extend(myLatlng2);
   
            google.maps.event.addListener(markerOption2, "click", (e) => {
                infoWindow.setContent("This is your current location");
                infoWindow.open(map, markerOption2);
                });
            
            // Automatically center the map fitting all markers on the screen
              map.fitBounds(bounds);
             map.panToBounds(bounds); 
            
        }
        
        
       // Adding Multiple marker on google map and bounded with all map
          for ( var i = 0; i < neighborhoods.length; i++) {
        var data = neighborhoods[i];	
        console.log(data);
        myLatlng = new google.maps.LatLng(data.lat, data.lng);
       
        bounds.extend(myLatlng);
        } 	
       
       // Code for onclick on tiles show marker lication center with infowindow
         markers = neighborhoods.map(function (location, i){	
           // var myLatlng = new google.maps.LatLng(location.lat, location.lng);  
           var marker = new google.maps.Marker({
                position: location,
                map: map,
                label: labels[i++ % labels.length],
                });
           
             google.maps.event.addListener(marker, 'mouseover', function(evt) {			  	
                    infoWin.setContent('<img src="images/'+location.logo+'" style="position: relative; width: 220px; height: 104px;">'+" <br/> <b> Company Name :</b>"+location.companyname+"<br/> <b> Email :</b>"+location.email+"<br/> <b class='text-success'> Open :</b>"+location.open);
                    infoWin.open(map, marker);
                 }) 
                 return marker;		 
         }); 		 
        
        //    var markerCluster = new MarkerClusterer(map, markers, {
        //        imagePath: 'https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m'
        //       });
    }

    window.initMap = initMap;




const SearchSitter = () => {

    const effectRan = useRef(false);

    useEffect(() => {
		if(effectRan.current === false){
		const script = document.createElement('script');
	  
		script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyAfL2oULbnrWbl_G-WdVcxGH8TfEme8dhk&libraries=places&callback=initMap";
		script.async = true;  
		document.body.appendChild(script);  
		return () => {
		  document.body.removeChild(script);
		  effectRan.current = true;
		}
	}
	  }, []);


    const [users, setUsers] = useState([]);
    const [searchAddress, setSearchAddress] = useState("");
    const [searchLat, setSearchLat] = useState("");
    const [searchLong, setSearchLong] = useState("");

    useEffect(() => {
      if(effectRan.current === false){
        findNearestLocation(); 
        return () => {
          effectRan.current = true;
        }
      }       
    }, []);

    function setNativeValue(element, value) {
      const valueSetter = Object.getOwnPropertyDescriptor(element, 'value').set;
      const prototype = Object.getPrototypeOf(element);
      const prototypeValueSetter = Object.getOwnPropertyDescriptor(prototype, 'value').set;
      
      if (valueSetter && valueSetter !== prototypeValueSetter) {
        prototypeValueSetter.call(element, value);
      } else {
        valueSetter.call(element, value);
      }
      }

    const onChangeSearchAddress = e => {
        const searchAddress = e.target.value; 
        setSearchAddress(searchAddress);
    };

    const onChangeSearchLat = e => {
      const searchLat = e.target.value; 
      setSearchLat(searchLat);
    };
    const onChangeSearchLong = e => {
      const searchLong = e.target.value; 
      setSearchLong(searchLong);
    };

    const findNearestLocation = () => {
        UserServices.findNearestLocation(searchAddress, searchLat, searchLong)
        .then(response => {
            setUsers(response.data);
            //console.log(response.data);
        });
    };

   
//        // Fetching records based on nearest location
//  var neighborhoods =  [
//      {lat: 28.64, lng: 77.24 }
//     ]; 



    // Code start for search list of nearest sitter

   
        
  return (
    <div>
        <h4 className="text-center">Search Sitter</h4>
        <div className="container">      
          <div className="input-group">
            <input type="search" name="address" id="map-search" className="form-control rounded" placeholder="Search" value={searchAddress} onChange={onChangeSearchAddress} aria-label="Search" aria-describedby="search-addon" required="required" />
            <input type="text" name="latitude" id="lat" className="form-control lat" value={searchLat} onChange={onChangeSearchLat}  style={{display: 'none'}}/>
            <input type="text" name="longitude" id="lng" className="form-control lng"  value={searchLong} onChange={onChangeSearchLong} style={{display: 'none'}}/>
            <button className="btn btn-outline-primary" type='button' onClick={findNearestLocation}>search</button>	  
          </div>
        <div id="floating-panel">
        </div>
      </div>

       {/************  Show list of sitter with nearest location  ***********************/}

      <div className="container" style={{marginTop: '10px'}}>
        <div className="row">
          <div className="col-sm-6">
            {users && users.length > 0 && users.map((user, index) =>(
              <a href="#" className="nav-link" key={user.id}>
              <div className="card" style={{width: '33rem'}}>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-4">
                      <img src="images/" alt="Card image" style={{height: '100px', width: '100px'}} />
                    </div>
                    <div className="col-sm-8">
                      <h6 className="card-subtitle text-muted">{user.address}</h6>
                      <p className="card-text text-muted">Hours: <span className="text-success">Open.</span>{user.open}</p>
                      <p className="card-text text-muted">Charge/hr : {user.chargesperhour}</p>								
                    </div>
                  </div>
                </div>
              </div>	
            </a>			

            ))}
            		
          </div>
          <div className="col-sm-6">
            {/*Google map*/}
            <div id="map-canvas-search" />
          </div>
        </div>
      </div>
    </div>
  )

}
export default SearchSitter;
