import React, {useState, useEffect} from 'react'
import {useParams, useNavigate} from 'react-router-dom'
import UserServices from '../../services/UserServices';

const EditSitter = (props) => {
    const {id} = useParams();
    let navigate = useNavigate();

    const initialUserService = {
        id: null,
		contactname: "",
		email: "",
		company: "",
		open: "",
		close: "",
		chargesperhour: ""
    };

    const [currentUser, setCurrentUser] = useState(initialUserService);
    const [message, setMessage] = useState("");

    const getUser = id => {
        UserServices.get(id)
          .then(response => {
            setCurrentUser(response.data);
            console.log(response.data);
          })
          .catch(e => {
            console.log(e);
          });
      };

      useEffect(() => {
        if (id)
        getUser(id);
      }, [id]);

      const handleInputChange = event => {
        const { name, value } = event.target;
        setCurrentUser({ ...currentUser, [name]: value });
      };

    //   const updateUser = () => {
    //     var data = {
    //       id: currentUser.id,
    //       contactname: currentUser.contactname,
    //       email: currentUser.email,
    //       company: currentUser.company,
    //       open: currentUser.open,
    //       close: currentUser.close,
    //       chargesperhour: currentUser.chargesperhour  
    //     };

    //     UserServices.update(currentUser.id, data)
    //     .then(response => {
    //         setCurrentUser({ ...currentUser});
    //       console.log(response.data);
    //     })
    //     .catch(e => {
    //       console.log(e);
    //     });
    // };
  
    const updateUser = () => {
        UserServices.update(currentUser.id, currentUser)
        .then(response => {
            setCurrentUser({ ...currentUser});
          console.log(response.data);
          setMessage("The tutorial was updated successfully!");
        })
        .catch(e => {
          console.log(e);
        });
    };

    const deleteUser = () => {
        UserServices.remove(currentUser.id)
          .then(response => {
            console.log(response.data);
            navigate("/admin/sitterlist");
          })
          .catch(e => {
            console.log(e);
          });
      };
      
  return (
    <div>
    {currentUser ? (
      <div className="edit-form">
        <h4>Edit Sitter</h4>
        <form>
          <div className="form-group">
            <label htmlFor="title">Contact Name</label>
            <input
              type="text"
              className="form-control"
              id="contactname"
              name="contactname"
              value={currentUser.contactname}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <input
              type="text"
              className="form-control"
              id="description"
              name="description"
              value={currentUser.description}
              onChange={handleInputChange}
            />
          </div>
        </form>
        <button className="badge badge-danger mr-2" onClick={deleteUser}>
          Delete
        </button>

        <button
          type="submit"
          className="badge badge-success"
          onClick={updateUser}
        >
          Update
        </button>
        <p>{message}</p>
      </div>
    ) : (
      <div>
        <br />
        <p>Please click on a Tutorial...</p>
      </div>
    )}
  </div>
  )
}

export default EditSitter;
